insert into
Kitap(isbn_no,kitap_adi,seri_adi,yazar_adi,yayin_adi,aciklama,resim)
values(6055411251,'Atatürk ve Demokratik Türkiye','','Prof. Dr. Halil İnalcık','Kırmızı Yayınları','Atatürkçülüğü yorumlarken, günlük siyasi ve sosyal akımların etkisiyle onaylama veya bağnaz tepkilerle karşılaşmak olanaklıdır. Toplumumuzu, uzlaşmaz iki kitle halinde karşı karşıya getiren derin anlaşmazlığın, felaketli sonuçlar getireceğini daima hatırda tutarak karşıtlığı çözmek, uzlaşma yollarını bulmak zorundayız. Yurdun geleceği için iki taraf da bağnaz, uzlaşmaz tavrından kurtulmak zorundadır. Demokrasi, toplumda barışı güvence altına almak için uzlaşma ve denge zeminidir.','');

insert into
